VALUES (4, null, 'Address', 0, 0, 1, '{"identifier":"core:Address","description":"Address represents an address of a city object.","table":"address","properties":[{"name":"objectId","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the identifier of the address that is unique within the database. Using a globally unique identifier is recommended.","value":{"column":"objectid","type":"string"}},{"name":"identifier","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies an optional identifier of the address that is globally unique.","value":{"column":"identifier","type":"string"}},{"name":"codeSpace","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the code space of the identifier, typically a reference to the maintaining authority.","value":{"column":"identifier_codespace","type":"string"},"parent":1},{"name":"street","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the name of the street.","value":{"column":"street","type":"string"}},{"name":"houseNumber","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the house number.","value":{"column":"house_number","type":"string"}},{"name":"poBox","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the post office box number.","value":{"column":"po_box","type":"string"}},{"name":"zipCode","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the ZIP code.","value":{"column":"zip_code","type":"string"}},{"name":"city","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the name of the city.","value":{"column":"city","type":"string"}},{"name":"state","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the name of the state.","value":{"column":"state","type":"string"}},{"name":"country","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the name of the country.","value":{"colu