INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (7, 6, 'AbstractCityObject', 1, 0, 1, '{"identifier":"core:AbstractCityObject","description":"AbstractCityObject is the abstract superclass of all thematic classes.","table":"feature","properties":[{"name":"externalReference","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"References external objects in other information systems that have a relation to the city object.","type":"core:ExternalReference"},{"name":"generalizesTo","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates generalized representations of the same real-world object in different Levels of Detail to the city object. The direction of this relation is from the city object to the corresponding generalized city objects.","type":"core:FeatureProperty","target":"core:AbstractCityObject"},{"name":"relativeToTerrain","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Describes the vertical position of the city object relative to the surrounding terrain.","type":"core:String"},{"name":"relativeToWater","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Describes the vertical position of the city object relative to the surrounding water surface.","type":"core:String"},{"name":"relatedTo","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates other city objects to the city object. It also describes how the city objects are related to each other.","type":"core:CityObjectRelation"},{"name":"appearance","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates appearances to the city object.","type":"core:AppearanceProperty"},{"name":"dynamizer","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates Dynamizer objects to the city object. These allow timeseries data to override static attribute values of the city object.","type":"core:FeatureProperty","target":"core:AbstractDynamizer"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (8, 7, 'AbstractSpace', 1, 0, 1, '{"identifier":"core:AbstractSpace","description":"AbstractSpace is the abstract superclass for all types of spaces. A space is an entity of volumetric extent in the real world.","table":"feature","properties":[{"name":"spaceType","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the degree of openness of a space.","type":"core:String"},{"name":"volume","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies qualified volumes related to the space.","type":"core:QualifiedVolume"},{"name":"area","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies qualified areas related to the space.","type":"core:QualifiedArea"},{"name":"boundary","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to surfaces that bound the space.","type":"core:FeatureProperty","target":"core:AbstractSpaceBoundary"},{"name":"lod0Point","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to a 3D Point geometry that represents the space in Level of Detail 0.","type":"core:GeometryProperty","target":"core:Point"},{"name":"lod0MultiSurface","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to a 3D MultiSurface geometry that represents the space in Level of Detail 0.","type":"core:GeometryProperty","target":"core:MultiSurface"},{"name":"lod0MultiCurve","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to a 3D MultiCurve geometry that represents the space in Level of Detail 0.","type":"core:GeometryProperty","target":"core:MultiLineString"},{"name":"lod1Solid","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to a 3D Solid geometry that represents the space in Level of Detail 1.","type":"core:GeometryProperty","target":"core:AbstractSolid"},{"name":"lod2Solid","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to a 3D Solid geometry that represents the space in Level of Detail 2.","type":"core:GeometryProperty","target":"core:AbstractSolid"},{"name":"lod2MultiSurface","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to a 3D MultiSurface geometry that represents the space in Level of Detail 2.","type":"core:GeometryProperty","target":"core:MultiSurface"},{"name":"lod2MultiCurve","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to a 3D MultiCurve geometry that represents the space in Level of Detail 2.","type":"core:GeometryProperty","target":"core:MultiLineString"},{"name":"lod3Solid","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to a 3D Solid geometry that represents the space in Level of Detail 3.","type":"core:GeometryProperty","target":"core:AbstractSolid"},{"name":"lod3MultiSurface","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to a 3D MultiSurface geometry that represents the space in Level of Detail 3.","type":"core:GeometryProperty","target":"core:MultiSurface"},{"name":"lod3MultiCurve","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to a 3D MultiCurve geometry that represents the space in Level of Detail 3.","type":"core:GeometryProperty","target":"core:MultiLineString"}]}');

