INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (21, null, 'ImplicitGeometry', 0, 0, 1, '{"identifier":"core:ImplicitGeometry","description":"ImplicitGeometry is a geometry representation where the shape is stored only once as a prototypical geometry. Examples are a tree or other vegetation object, a traffic light or a traffic sign. This prototypic geometry object can be re-used or referenced many times, wherever the corresponding feature occurs in the 3D city model.","table":"implicit_geometry","properties":[{"name":"objectId","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the identifier of the implicit geometry that is unique within the database. Using a globally unique identifier is recommended.","value":{"column":"objectid","type":"string"}},{"name":"mimeType","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the MIME type of the external file that stores the prototypical geometry.","value":{"column":"mime_type","type":"string"}},{"name":"codeSpace","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the code space of the MIME type, typically a dictionary, thesaurus, classification scheme, authority, or pattern for the MIME type.","value":{"column":"mime_type_codespace","type":"string"},"parent":1},{"name":"libraryObject","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the URI that points to the prototypical geometry stored in an external file.","value":{"column":"reference_to_library","type":"string"}},{"name":"relativeGeometry","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates to a prototypical geometry in a local coordinate system stored in the database.","target":"core:AbstractGeometry","join":{"table":"geometry_data","fromColumn":"relative_geometry_id","toColumn":"id"}},{"name":"appearance","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Relates appearances to the ImplicitGeometry.","join":{"table":"appearance","fromColumn":"id","toColumn":"implicit_geometry_id"}}]}');

-- Dynamizer Module --

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (100, 16, 'Dynamizer', 0, 0, 2, '{"identifier":"dyn:Dynamizer","description":"A Dynamizer is an object that injects timeseries data for an individual attribute of the city object in which it is included. The timeseries data overrides the static value of the referenced city object attribute in order to represent dynamic (time-dependent) variations of its value.","table":"feature","properties":[{"name":"attributeRef","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the attribute of a feature whose value is overridden or replaced by the (dynamic) values specified by the Dynamizer.","type":"core:String"},{"name":"startTime","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the beginning of the time span for which the Dynamizer provides dynamic values.","type":"core:Timestamp"},{"name":"endTime","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the end of the time span for which the Dynamizer provides dynamic values.","type":"core:Timestamp"},{"name":"dynamicData","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Relates to the timeseries data.","type":"core:FeatureProperty","target":"dyn:AbstractTimeseries"},{"name":"sensorConnection","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Relates to the sensor API that delivers timeseries data.","type":"dyn:SensorConnection"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (101, 3, 'AbstractTimeseries', 1, 0, 2, '{"identifier":"dyn:AbstractTimeseries","description":"AbstractTimeseries is the abstract superclass representing any type of timeseries data.","table":"feature","properties":[{"name":"firstTimestamp","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the beginning of the timeseries.","type":"core:Timestamp"},{"name":"lastTimestamp","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the end of the timeseries.","type":"core:Timestamp"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (102, 101, 'AbstractAtomicTimeseries', 1, 0, 2, '{"identifier":"dyn:AbstractAtomicTimeseries","description":"AbstractAtomicTimeseries represents the attributes and relationships that are common to all kinds of atomic timeseries (GenericTimeseries, TabulatedFileTimeseries, StandardFileTimeseries). An atomic timeseries represents time-varying data of a specific data type for a single contiguous time interval.","table":"feature","properties":[{"name":"observationProperty","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the phenomenon for which the atomic timeseries provides observation values.","type":"core:String"},{"name":"uom","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the unit of measurement of the observation values.","type":"core:String"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (103, 102, 'TabulatedFileTimeseries', 0, 0, 2, '{"identifier":"dyn:TabulatedFileTimeseries","description":"A TabulatedFileTimeseries represents time-varying data of a specific data type for a single contiguous time interval. The data is provided in an external file referenced in the TabulatedFileTimeseries. The file contains table structured data using an appropriate file format such as comma-separated values (CSV), Microsoft Excel (XLSX) or Google Spreadsheet. The timestamps and the values are given in specific columns of the table. Each row represents a single time-value-pair. A subset of rows can be selected using the idColumn and idValue attributes.","table":"feature","properties":[{"name":"fileLocation","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the URI that points to the external timeseries file.","type":"core:URI"},{"name":"fileType","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the format used to represent the timeseries data.","type":"core:Code"},{"name":"mimeType","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the MIME type of the external timeseries file.","type":"core:Code"},{"name":"valueType","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Indicates the specific type of the timeseries data.","type":"core:String"},{"name":"numberOfHeaderLines","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Indicates the number of lines at the beginning of the tabulated file that represent headers.","type":"core:Integer"},{"name":"fieldSeparator","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Indicates which symbol is used to separate the individual values in the tabulated file.","type":"core:String"},{"name":"decimalSymbol","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Indicates which symbol is used to separate the integer part from the fractional part of a decimal number.","type":"core:String"},{"name":"idColumnNo","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the number of the column that stores the identifier of the time-value-pair.","type":"core:Integer"},{"name":"idColumnName","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the name of the column that stores the identifier of the time-value-pair.","type":"core:String"},{"name":"idValue","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the value of the identifier for which the time-value-pairs are to be selected.","type":"core:String"},{"name":"timeColumnNo","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the number of the column that stores the timestamp of the time-value-pair.","type":"core:Integer"},{"name":"timeColumnName","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the name of the column that stores the timestamp of the time-value-pair.","type":"core:String"},{"name":"valueColumnNo","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the number of the column that stores the value of the time-value-pair.","type":"core:Integer"},{"name":"valueColumnName","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the name of the column that stores the value of the time-value-pair.","type":"core:String"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (104, 102, 'StandardFileTimeseries', 0, 0, 2, '{"identifier":"dyn:StandardFileTimeseries","description":"A StandardFileTimeseries represents time-varying data for a single contiguous time interval. The data is provided in an external file referenced in the StandardFileTimeseries. The data within the external file is encoded according to a dedicated format for the representation of timeseries data such as using the OGC TimeseriesML or OGC Observations &amp; Measurements Standard. The data type of the data has to be specified within the external file.","table":"feature","properties":[{"name":"fileLocation","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the URI that points to the external timeseries file.","type":"core:URI"},{"name":"fileType","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the format used to represent the timeseries data.","type":"core:Code"},{"name":"mimeType","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Specifies the MIME type of the external timeseries file.","type":"core:Code"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (105, 102, 'GenericTimeseries', 0, 0, 2, '{"identifier":"dyn:GenericTimeseries","description":"A GenericTimeseries represents time-varying data in the form of embedded time-value-pairs of a specific data type for a single contiguous time interval.","table":"feature","properties":[{"name":"valueType","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Indicates the specific type of all time-value-pairs that are part of the GenericTimeseries.","type":"core:String"},{"name":"timeValuePair","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Relates to the time-value-pairs that are part of the GenericTimeseries.","type":"dyn:TimeValuePair"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (106, 101, 'CompositeTimeseries', 0, 0, 2, '{"identifier":"dyn:CompositeTimeseries","description":"A CompositeTimeseries is a (possibly recursive) aggregation of atomic and composite timeseries. The components of a composite timeseries must have non-overlapping time intervals.","table":"feature","properties":[{"name":"component","namespace":"http://3dcitydb.org/3dcitydb/dynamizer/5.0","description":"Relates to the atomic and composite timeseries that are part of the CompositeTimeseries. The referenced timeseries are sequentially ordered.","type":"dyn:TimeseriesComponent"}]}');

