INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (600, 11, 'AbstractTransportationSpace', 1, 0, 7, '{"identifier":"tran:AbstractTransportationSpace","description":"AbstractTransportationSpace is the abstract superclass of transportation objects such as Roads, Tracks, Railways, Waterways or Squares.","table":"feature","properties":[{"name":"trafficDirection","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Indicates the direction of traffic flow relative to the corresponding linear geometry representation.","type":"core:String"},{"name":"occupancy","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Provides information on the residency of persons, vehicles, or other moving features in the transportation space.","type":"core:Occupancy"},{"name":"trafficSpace","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Relates to the traffic spaces that are part of the transportation space.","type":"core:FeatureProperty","target":"tran:TrafficSpace"},{"name":"auxiliaryTrafficSpace","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Relates to the auxiliary traffic spaces that are part of the transportation space.","type":"core:FeatureProperty","target":"tran:AuxiliaryTrafficSpace"},{"name":"hole","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Relates to the holes that are part of the transportation space.","type":"core:FeatureProperty","target":"tran:Hole"},{"name":"marking","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Relates to the markings that are part of the transportation space.","type":"core:FeatureProperty","target":"tran:Marking"},{"name":"lod1MultiSurface","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D MultiSurface geometry that represents the transportation space in Level of Detail 1. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:MultiSurface"},{"name":"lod4MultiSurface","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D MultiSurface geometry that represents the transportation space in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:MultiSurface"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (601, 600, 'Railway', 0, 1, 7, '{"identifier":"tran:Railway","description":"A Railway is a transportation space used by wheeled vehicles on rails.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Indicates the specific type of the Railway.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Specifies the intended purposes of the Railway.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Specifies the actual uses of the Railway.","type":"core:Code"},{"name":"section","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Relates to the sections that are part of the Railway.","type":"core:FeatureProperty","target":"tran:Section"},{"name":"intersection","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Relates to the intersections that are part of the Railway.","type":"core:FeatureProperty","target":"tran:Intersection"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
