INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (609, 11, 'ClearanceSpace', 0, 0, 7, '{"identifier":"tran:ClearanceSpace","description":"A ClearanceSpace represents the actual free space above a TrafficArea within which a mobile object can move without contacting an obstruction.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Indicates the specific type of the ClearanceSpace.","type":"core:Code"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (610, 11, 'TrafficSpace', 0, 0, 7, '{"identifier":"tran:TrafficSpace","description":"A TrafficSpace is a space in which traffic takes place. Traffic includes the movement of entities such as trains, vehicles, pedestrians, ships, or other transportation types.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Indicates the specific type of the TrafficSpace.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Specifies the intended purposes of the TrafficSpace.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Specifies the actual uses of the TrafficSpace.","type":"core:Code"},{"name":"granularity","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Defines whether traffic spaces are represented by individual ways or by individual lanes, depending on the desired level of spatial and semantic decomposition.","type":"core:String"},{"name":"trafficDirection","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Indicates the direction of traffic flow relative to the corresponding linear geometry representation.","type":"core:String"},{"name":"occupancy","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Provides information on the residency of persons, vehicles, or other moving features in the TrafficSpace.","type":"core:Occupancy"},{"name":"predecessor","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Indicates the predecessor(s) of the TrafficSpace.","type":"core:FeatureProperty","target":"tran:TrafficSpace"},{"name":"successor","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Indicates the successor(s) of the TrafficSpace.","type":"core:FeatureProperty","target":"tran:TrafficSpace"},{"name":"clearanceSpace","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Relates to the clearance spaces that are part of the TrafficSpace.","type":"core:FeatureProperty","target":"tran:ClearanceSpace"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (611, 11, 'Hole', 0, 0, 7, '{"identifier":"tran:Hole","description":"A Hole is an opening in the surface of a Road, Track or Square such as road damages, manholes or drains. Holes can span multiple transportation objects.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Indicates the specific type of the Hole.","type":"core:Code"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (612, 14, 'AuxiliaryTrafficArea', 0, 0, 7, '{"identifier":"tran:AuxiliaryTrafficArea","description":"An AuxiliaryTrafficArea is the ground surface of an AuxiliaryTrafficSpace.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Indicates the specific type of the AuxiliaryTrafficArea.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Specifies the intended purposes of the AuxiliaryTrafficArea.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Specifies the actual uses of the AuxiliaryTrafficArea.","type":"core:Code"},{"name":"surfaceMaterial","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Specifies the type of pavement of the AuxiliaryTrafficArea.","type":"core:Code"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (613, 14, 'TrafficArea', 0, 0, 7, '{"identifier":"tran:TrafficArea","description":"A TrafficArea is the ground surface of a TrafficSpace. Traffic areas are the surfaces upon which traffic actually takes place.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Indicates the specific type of the TrafficArea.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Specifies the intended purposes of the TrafficArea.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Specifies the actual uses of the TrafficArea.","type":"core:Code"},{"name":"surfaceMaterial","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Specifies the type of pavement of the TrafficArea.","type":"core:Code"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (614, 14, 'Marking', 0, 0, 7, '{"identifier":"tran:Marking","description":"A Marking is a visible pattern on a transportation area relevant to the structuring or restriction of traffic. Examples are road markings and markings related to railway or waterway traffic.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/transportation/5.0","description":"Indicates the specific type of the Marking.","type":"core:Code"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (615, 14, 'HoleSurface', 0, 0, 7, '{"identifier":"tran:HoleSurface","description":"A HoleSurface is a representation of the ground surface of a hole.","table":"feature"}');

-- Construction Module --

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (700, 12, 'AbstractConstruction', 1, 0, 8, '{"identifier":"con:AbstractConstruction","description":"AbstractConstruction is the abstract superclass for objects that are manufactured by humans from construction materials, are connected to earth, and are intended to be permanent. A connection with the ground also exists when the construction rests by its own weight on the ground or is moveable limited on stationary rails or if the construction  is intended to be used mainly stationary.","table":"feature","properties":[{"name":"conditionOfConstruction","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Indicates the life-cycle status of the construction.","type":"core:String"},{"name":"dateOfConstruction","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Indicates the date at which the construction was completed.","type":"core:Timestamp"},{"name":"dateOfDemolition","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Indicates the date at which the construction was demolished.","type":"core:Timestamp"},{"name":"constructionEvent","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Describes specific events in the life-time of the construction.","type":"con:ConstructionEvent"},{"name":"elevation","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Specifies qualified elevations of the construction in relation to a well-defined surface which is commonly taken as origin (e.g. geoid or water level).","type":"con:Elevation"},{"name":"height","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Specifies qualified heights of the construction above ground or below ground.","type":"con:Height"},{"name":"occupancy","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Provides qualified information on the residency of persons, animals, or other moveable objects in the construction.","type":"core:Occupancy"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (701, 700, 'OtherConstruction', 0, 1, 8, '{"identifier":"con:OtherConstruction","description":"An OtherConstruction is a construction that is not covered by any of the other subclasses of AbstractConstruction.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Indicates the specific type of the OtherConstruction.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Specifies the intended purposes of the OtherConstruction.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Specifies the actual uses of the OtherConstruction.","type":"core:Code"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (702, 12, 'AbstractConstructiveElement', 1, 0, 8, '{"identifier":"con:AbstractConstructiveElement","description":"AbstractConstructiveElement is the abstract superclass for the representation of volumetric elements of a construction. Examples are walls, beams, slabs.","table":"feature","properties":[{"name":"isStructuralElement","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Indicates whether the constructive element is essential from a structural point of view. A structural element cannot be omitted without collapsing of the construction. Examples are pylons and anchorages of bridges.","type":"core:Boolean"},{"name":"filling","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Relates to the elements that fill the opening of the constructive element.","type":"core:FeatureProperty","target":"con:AbstractFillingElement"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (703, 12, 'AbstractFillingElement', 1, 0, 8, '{"identifier":"con:AbstractFillingElement","description":"AbstractFillingElement is the abstract superclass for different kinds of elements that fill the openings of a construction.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (704, 703, 'Window', 0, 0, 8, '{"identifier":"con:Window","description":"A Window is a construction for closing an opening in a wall  or roof, primarily intended to admit light and/or provide ventilation.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Indicates the specific type of the Window.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Specifies the intended purposes of the Window.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Specifies the actual uses of the Window","type":"core:Code"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (705, 703, 'Door', 0, 0, 8, '{"identifier":"con:Door","description":"A Door is a construction for closing an opening intended primarily for access or egress or both.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Indicates the specific type of the Door.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Specifies the intended purposes of the Door.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Specifies the actual uses of the Door.","type":"core:Code"},{"name":"address","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Relates to the addresses that are assigned to the Door.","type":"core:AddressProperty"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (706, 12, 'AbstractFurniture', 1, 0, 8, '{"identifier":"con:AbstractFurniture","description":"AbstractFurniture is the abstract superclass for the representation of furniture objects of a construction.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (707, 12, 'AbstractInstallation', 1, 0, 8, '{"identifier":"con:AbstractInstallation","description":"AbstractInstallation is the abstract superclass for the representation of installation objects of a construction.","table":"feature","properties":[{"name":"relationToConstruction","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Indicates whether the installation is located inside and/or outside of the construction.","type":"core:String"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (708, 14, 'AbstractConstructionSurface', 1, 0, 8, '{"identifier":"con:AbstractConstructionSurface","description":"AbstractConstructionSurface is the abstract superclass for different kinds of surfaces that bound a construction.","table":"feature","properties":[{"name":"fillingSurface","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Relates to the surfaces that seal the openings of the construction surface.","type":"core:FeatureProperty","target":"con:AbstractFillingSurface"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (709, 708, 'WallSurface', 0, 0, 8, '{"identifier":"con:WallSurface","description":"A WallSurface is a surface that is part of the building facade visible from the outside.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (710, 708, 'GroundSurface', 0, 0, 8, '{"identifier":"con:GroundSurface","description":"A GroundSurface is a surface that represents the ground plate of a construction. The polygon defining the ground plate is congruent with the footprint of the construction.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (711, 708, 'InteriorWallSurface', 0, 0, 8, '{"identifier":"con:InteriorWallSurface","description":"An InteriorWallSurface is a surface that is visible from inside a construction. An example is the wall of a room.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (712, 708, 'RoofSurface', 0, 0, 8, '{"identifier":"con:RoofSurface","description":"A RoofSurface is a surface that delimits major roof parts of a construction.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (713, 708, 'FloorSurface', 0, 0, 8, '{"identifier":"con:FloorSurface","description":"A FloorSurface is surface that represents the interior floor of a construction. An example is the floor of a room.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (714, 708, 'OuterFloorSurface', 0, 0, 8, '{"identifier":"con:OuterFloorSurface","description":"An OuterFloorSurface is a surface that belongs to the outer construction shell with the orientation pointing upwards. An example is the floor of a loggia.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (715, 708, 'CeilingSurface', 0, 0, 8, '{"identifier":"con:CeilingSurface","description":"A CeilingSurface is a surface that represents the interior ceiling of a construction. An example is the ceiling of a room.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (716, 708, 'OuterCeilingSurface', 0, 0, 8, '{"identifier":"con:OuterCeilingSurface","description":"An OuterCeilingSurface is a surface that belongs to the outer building shell with the orientation pointing downwards. An example is the ceiling of a loggia.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (717, 14, 'AbstractFillingSurface', 1, 0, 8, '{"identifier":"con:AbstractFillingSurface","description":"AbstractFillingSurface is the abstract superclass for different kinds of surfaces that seal openings filled by filling elements.","table":"feature","properties":[{"name":"lod3ImplicitRepresentation","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to an implicit geometry that represents the AbstractFillingSurface in Level of Detail 3. Deprecated since CityGML 3.0.","type":"core:ImplicitGeometryProperty"},{"name":"lod4ImplicitRepresentation","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to an implicit geometry that represents the AbstractFillingSurface in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:ImplicitGeometryProperty"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (718, 717, 'DoorSurface', 0, 0, 8, '{"identifier":"con:DoorSurface","description":"A DoorSurface is either a boundary surface of a Door feature or a surface that seals an opening filled by a door.","table":"feature","properties":[{"name":"address","namespace":"http://3dcitydb.org/3dcitydb/construction/5.0","description":"Relates to the addresses that are assigned to the DoorSurface.","type":"core:AddressProperty"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (719, 717, 'WindowSurface', 0, 0, 8, '{"identifier":"con:WindowSurface","description":"A WindowSurface is either a boundary surface of a Window feature or a surface that seals an opening filled by a window.","table":"feature"}');

-- Tunnel Module --

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (800, 700, 'AbstractTunnel', 1, 0, 9, '{"identifier":"tun:AbstractTunnel","description":"AbstractTunnel is an abstract superclass representing the common attributes and associations of the classes Tunnel and TunnelPart.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Indicates the specific type of the Tunnel or TunnelPart.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Specifies the intended purposes of the Tunnel or TunnelPart.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Specifies the actual uses of the Tunnel or TunnelPart.","type":"core:Code"},{"name":"tunnelConstructiveElement","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Relates the constructive elements to the Tunnel or TunnelPart.","type":"core:FeatureProperty","target":"tun:TunnelConstructiveElement"},{"name":"tunnelInstallation","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Relates the installation objects to the Tunnel or TunnelPart.","type":"core:FeatureProperty","target":"tun:TunnelInstallation"},{"name":"hollowSpace","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Relates the hollow spaces to the Tunnel or TunnelPart.","type":"core:FeatureProperty","target":"tun:HollowSpace"},{"name":"tunnelFurniture","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Relates the furniture objects to the Tunnel or TunnelPart.","type":"core:FeatureProperty","target":"tun:TunnelFurniture"},{"name":"lod1MultiSurface","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D MultiSurface geometry that represents the Tunnel or TunnelPart in Level of Detail 1. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:MultiSurface"},{"name":"lod4MultiCurve","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D MultiCurve geometry that represents the Tunnel or TunnelPart in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:MultiLineString"},{"name":"lod4MultiSurface","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D MultiSurface geometry that represents the Tunnel or TunnelPart in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:MultiSurface"},{"name":"lod4Solid","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D Solid geometry that represents the Tunnel or TunnelPart in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:AbstractSolid"},{"name":"lod4TerrainIntersectionCurve","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D MultiCurve geometry that represents the terrain intersection curve of the Tunnel or TunnelPart in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:MultiLineString"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (801, 800, 'Tunnel', 0, 1, 9, '{"identifier":"tun:Tunnel","description":"A Tunnel represents a horizontal or sloping enclosed passage way of a certain length, mainly underground or underwater.","table":"feature","properties":[{"name":"tunnelPart","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Relates the tunnel parts to the Tunnel.","type":"core:FeatureProperty","target":"tun:TunnelPart"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (802, 800, 'TunnelPart', 0, 0, 9, '{"identifier":"tun:TunnelPart","description":"A TunnelPart is a physical or functional subdivision of a Tunnel. It would be considered a Tunnel, if it were not part of a collection of other TunnelParts.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (803, 702, 'TunnelConstructiveElement', 0, 0, 9, '{"identifier":"tun:TunnelConstructiveElement","description":"A TunnelConstructiveElement is an element of a Tunnel which is essential from a structural point of view. Examples are walls, slabs, beams.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Indicates the specific type of the TunnelConstructiveElement.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Specifies the intended purposes of the TunnelConstructiveElement.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Specifies the actual uses of the TunnelConstructiveElement.","type":"core:Code"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (804, 11, 'HollowSpace', 0, 0, 9, '{"identifier":"tun:HollowSpace","description":"A HollowSpace is a space within a Tunnel or TunnelPart intended for certain functions (e.g. transport or passage ways, service rooms, emergency shelters). A HollowSpace is bounded physically and/or virtually (e.g. by ClosureSurfaces or GenericSurfaces).","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Indicates the specific type of the HollowSpace.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Specifies the intended purposes of the HollowSpace.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Specifies the actual uses of the HollowSpace.","type":"core:Code"},{"name":"tunnelFurniture","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Relates the furniture objects to the HollowSpace.","type":"core:FeatureProperty","target":"tun:TunnelFurniture"},{"name":"tunnelInstallation","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Relates the installation objects to the HollowSpace.","type":"core:FeatureProperty","target":"tun:TunnelInstallation"},{"name":"lod4Solid","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D Solid geometry that represents the HollowSpace in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:AbstractSolid"},{"name":"lod4MultiSurface","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D MultiSurface geometry that represents the HollowSpace in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:MultiSurface"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (805, 707, 'TunnelInstallation', 0, 0, 9, '{"identifier":"tun:TunnelInstallation","description":"A TunnelInstallation is a permanent part of a Tunnel (inside and/or outside) which does not have the significance of a TunnelPart. In contrast to TunnelConstructiveElement, a TunnelInstallation is not essential from a structural point of view. Examples are stairs, antennas or railings.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Indicates the specific type of the TunnelInstallation.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Specifies the intended purposes of the TunnelInstallation.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Specifies the actual uses of the TunnelInstallation.","type":"core:Code"},{"name":"lod2Geometry","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D geometry that represents the TunnelInstallation in Level of Detail 2. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:AbstractGeometry"},{"name":"lod3Geometry","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D geometry that represents the TunnelInstallation in Level of Detail 3. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:AbstractGeometry"},{"name":"lod4Geometry","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D geometry that represents the TunnelInstallation in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:AbstractGeometry"},{"name":"lod4ImplicitRepresentation","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to an implicit geometry that represents the TunnelInstallation in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:ImplicitGeometryProperty"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (806, 706, 'TunnelFurniture', 0, 0, 9, '{"identifier":"tun:TunnelFurniture","description":"A TunnelFurniture is an equipment for occupant use, usually not fixed to the tunnel.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Indicates the specific type of the TunnelFurniture.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Specifies the intended purposes of the TunnelFurniture.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/tunnel/5.0","description":"Specifies the actual uses of the TunnelFurniture.","type":"core:Code"},{"name":"lod4Geometry","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to a 3D geometry that represents the TunnelFurniture in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:GeometryProperty","target":"core:AbstractGeometry"},{"name":"lod4ImplicitRepresentation","namespace":"http://3dcitydb.org/3dcitydb/deprecated/5.0","description":"Relates to an implicit geometry that represents the TunnelFurniture in Level of Detail 4. Deprecated since CityGML 3.0.","type":"core:ImplicitGeometryProperty"}]}');

