INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (901, 900, 'Building', 0, 1, 10, '{"identifier":"bldg:Building","description":"A Building is a free-standing, self-supporting construction that is roofed, usually walled, and can be entered by humans and is normally designed to stand permanently in one place. It is intended for human occupancy (e.g. a place of work or recreation), habitation and/or shelter of humans, animals or things.","table":"feature","properties":[{"name":"buildingPart","namespace":"http://3dcitydb.org/3dcitydb/building/5.0","description":"Relates the building parts to the Building.","type":"core:FeatureProperty","target":"bldg:BuildingPart"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (902, 900, 'BuildingPart', 0, 0, 10, '{"identifier":"bldg:BuildingPart","description":"A BuildingPart is a physical or functional subdivision of a Building. It would be considered a Building, if it were not part of a collection of other BuildingParts.","table":"feature"}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (903, 702, 'BuildingConstructiveElement', 0, 0, 10, '{"identifier":"bldg:BuildingConstructiveElement","description":"A BuildingConstructiveElement is an element of a Building which is essential from a structural point of view. Examples are walls, slabs, staircases, beams.","table":"feature","properties":[{"name":"class","namespace":"http://3dcitydb.org/3dcitydb/building/5.0","description":"Indicates the specific type of the BuildingConstructiveElement.","type":"core:Code"},{"name":"function","namespace":"http://3dcitydb.org/3dcitydb/building/5.0","description":"Specifies the intended purposes of the BuildingConstructiveElement.","type":"core:Code"},{"name":"usage","namespace":"http://3dcitydb.org/3dcitydb/building/5.0","description":"Specifies the actual uses of the BuildingConstructiveElement.","type":"core:Code"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
