-- Appearance Module --

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (1100, 20, 'Appearance', 0, 0, 12, '{"identifier":"app:Appearance","description":"An Appearance is a collection of surface data, i.e. observable properties for surface geometry objects in the form of textures and material.","table":"appearance","properties":[{"name":"objectId","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the identifier of the appearance that is unique within the database. Using a globally unique identifier is recommended.","value":{"column":"objectid","type":"string"}},{"name":"identifier","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies an optional identifier of the appearance that is globally unique.","value":{"column":"identifier","type":"string"}},{"name":"codeSpace","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the code space of the identifier, typically a reference to the maintaining authority.","value":{"column":"identifier_codespace","type":"string"},"parent":1},{"name":"theme","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the topic of the Appearance. Each Appearance contains surface data for one theme only. Examples of themes are infrared radiation, noise pollution, or earthquake-induced structural stress.","value":{"column":"theme","type":"string"}},{"name":"isGlobal","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Indicates whether the Appearance is global, i.e. is not associated with a specific feature.","value":{"column":"is_global","type":"boolean"}},{"name":"surfaceData","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Relates to the surface data that are part of the Appearance.","target":"app:AbstractSurfaceData","joinTable":{"table":"appear_to_surface_data","sourceJoin":{"table":"appearance","fromColumn":"id","toColumn":"appearance_id"},"targetJoin":{"table":"surface_data","fromColumn":"surface_data_id","toColumn":"id","conditions":[{"column":"objectclass_id","value":"@target.objectclass_id@","type":"integer"}]}}}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (1101, null, 'AbstractSurfaceData', 1, 0, 12, '{"identifier":"app:AbstractSurfaceData","description":"AbstractSurfaceData is the abstract superclass for different kinds of textures and material.","table":"surface_data","properties":[{"name":"objectId","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the identifier of the surface data that is unique within the database. Using a globally unique identifier is recommended.","value":{"column":"objectid","type":"string"}},{"name":"identifier","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies an optional identifier of the surface data that is globally unique.","value":{"column":"identifier","type":"string"}},{"name":"codeSpace","namespace":"http://3dcitydb.org/3dcitydb/core/5.0","description":"Specifies the code space of the identifier, typically a reference to the maintaining authority.","value":{"column":"identifier_codespace","type":"string"},"parent":1},{"name":"isFront","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Indicates whether the texture or material is assigned to the front side or the back side of the surface geometry object.","value":{"column":"is_front","type":"boolean"}}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (1102, 1101, 'X3DMaterial', 0, 0, 12, '{"identifier":"app:X3DMaterial","description":"X3DMaterial defines properties for surface geometry objects based on the material definitions from the X3D and COLLADA standards.","table":"surface_data","properties":[{"name":"shininess","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the sharpness of the specular highlight.","value":{"column":"x3d_shininess","type":"double"}},{"name":"transparency","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the degree of transparency of the surface geometry object.","value":{"column":"x3d_transparency","type":"double"}},{"name":"ambientIntensity","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the minimum percentage of diffuseColor that is visible regardless of light sources.","value":{"column":"x3d_ambient_intensity","type":"double"}},{"name":"specularColor","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the color of the light directly reflected by the surface geometry object.","value":{"column":"x3d_specular_color","type":"string"}},{"name":"diffuseColor","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the color of the light diffusely reflected by the surface geometry object.","value":{"column":"x3d_diffuse_color","type":"string"}},{"name":"emissiveColor","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the color of the light emitted by the surface geometry object.","value":{"column":"x3d_emissive_color","type":"string"}},{"name":"isSmooth","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies which interpolation method is used for the shading of the surface geometry object. If the attribute is set to true, vertex normals should be used for shading (Gouraud shading). Otherwise, normals should be constant for a surface patch (flat shading).","value":{"column":"x3d_is_smooth","type":"boolean"}}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (1103, 1101, 'AbstractTexture', 1, 0, 12, '{"identifier":"app:AbstractTexture","description":"AbstractTexture is the abstract superclass to represent the common attributes of the classes ParameterizedTexture and GeoreferencedTexture.","table":"surface_data","properties":[{"name":"imageURI","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the URI that points to the external texture image.","value":{"column":"image_uri","type":"string"},"join":{"table":"tex_image","fromColumn":"tex_image_id","toColumn":"id"}},{"name":"mimeType","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the MIME type of the texture image.","value":{"column":"mime_type","type":"string"},"join":{"table":"tex_image","fromColumn":"tex_image_id","toColumn":"id"}},{"name":"codeSpace","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the code space of the MIME type, typically a dictionary, thesaurus, classification scheme, authority, or pattern for the MIME type.","value":{"column":"mime_type_codespace","type":"string"},"parent":1},{"name":"wrapMode","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the behaviour of the texture when the texture is smaller than the surface to which it is applied.","value":{"column":"tex_wrap_mode","type":"string"}},{"name":"borderColor","namespace":"http://3dcitydb.org/3dcitydb/appearance/5.0","description":"Specifies the color of that part of the surface that is not covered by the texture.","value":{"column":"tex_border_color","type":"string"}}]}');

