INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (1400, 19, 'Version', 0, 0, 15, '{"identifier":"vers:Version","description":"Version represents a defined state of a city model consisting of the dedicated versions of all city object instances that belong to the respective city model version. Versions can have names, a description and can be labeled with an arbitrary number of user defined tags.","table":"feature","properties":[{"name":"tag","namespace":"http://3dcitydb.org/3dcitydb/versioning/5.0","description":"Allows for adding keywords to the city model version.","type":"core:String"},{"name":"versionMember","namespace":"http://3dcitydb.org/3dcitydb/versioning/5.0","description":"Relates to all city objects that are part of the city model version.","type":"core:FeatureProperty","target":"core:AbstractFeatureWithLifespan"}]}');

INSERT INTO objectclass (ID, SUPERCLASS_ID, CLASSNAME, IS_ABSTRACT, IS_TOPLEVEL, NAMESPACE_ID, SCHEMA)
VALUES (1401, 17, 'VersionTransition', 0, 0, 15, '{"identifier":"vers:VersionTransition","description":"VersionTransition describes the change of the state of a city model from one version to another. Version transitions can have names, a description and can be further qualified by a type and a reason.","table":"feature","properties":[{"name":"reason","namespace":"http://3dcitydb.org/3dcitydb/versioning/5.0","description":"Specifies why the VersionTransition has been carried out.","type":"core:String"},{"name":"clonePredecessor","namespace":"http://3dcitydb.org/3dcitydb/versioning/5.0","description":"Indicates whether the set of city object instances belonging to the successor version of the city model is either explicitly enumerated within the successor version object (attribute clonePredecessor=false), or has to be derived from the modifications of the city model provided as a list of transactions on the city object versions contained in the predecessor version (attribute clonePredecessor=true).","type":"core:Boolean"},{"name":"type","namespace":"http://3dcitydb.org/3dcitydb/versioning/5.0","description":"Indicates the specific type of the VersionTransition.","type":"core:String"},{"name":"from","namespace":"http://3dcitydb.org/3dcitydb/versioning/5.0","description":"Relates to the predecessor version of the VersionTransition.","type":"core:FeatureProperty","target":"vers:Version"},{"name":"to","namespace":"http://3dcitydb.org/3dcitydb/versioning/5.0","description":"Relates to the successor version of the VersionTransition.","type":"core:FeatureProperty","target":"vers:Version"},{"name":"transaction","namespace":"http://3dcitydb.org/3dcitydb/versioning/5.0","description":"Relates to all transactions that have been applied as part of the VersionTransition.","type":"vers:Transaction"}]}');

